            // setCurrActivities(currActivities[activityID].users_id.splice(usersID.indexOf(userID)))
        :
        console.log(userID);

    }

    // handleAddUser
    // handleAddActivity
    return (
        <div>
            <table className="party-table">
                <thead>
                <UsersRow users={currUsers}/>
                </thead>
                <tbody>
                <Activities activities={currActivities} users={currUsers} handleCheckboxClick={handleCheckboxClick}/>
                </tbody>
            </table>
        </div>
    );
}

export default PartyTable;
