import React, {useEffect, useState} from "react";
import './App.css';
import PartyTable from "./PartyTable"
import ActivityTable from "./ActivityTable"

function App() {
    const [currParty, setCurrParty] = useState([]);
    const [currUsers, setCurrUsers] = useState([]);
    const [currActivities, setCurrActivities] = useState([]);
    const [selectedActivity, setSelectedActivity] = useState();
    const [currItems, setCurrItems] = useState([]);

    const loadCurrParty = () => {
        fetch('/get_recent_party')
            .then(res => res.json())
            .then(json => setCurrParty(json))
            .catch(ex => console.log(ex));
    }

    useEffect(() => {
        let mounted = true;
        if (mounted) {
            loadCurrParty();
            getCurrUsers();
            getCurrActivities();
            getCurrItems();
        }
        return () => {
            mounted = false;
        };
    }, []);

    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users));
    }

    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities));
    }

    const getCurrItems = () => {
        fetch('/get_items')
            .then(res => res.json())
            .then(json => setCurrItems(json.items));
    }

    // last_viewed patch request
    // edit mode
    // jsons may be and are empty in the beginning

    // the rendered activity (passed as props) should be the part of currActivities
    const handleActivityClick = (activity) => {
        // set property of an activity!
        // or! in activities, there is a property with an id of the selected one
        setSelectedActivity(activity);
    }

    const handleParticipationClick = (e, activityID, userID) => {
        setCurrActivities(prevState => prevState.map(obj => {
            if (obj.activity_id === activityID) {
                const updatedUsersID = [...obj.users_id];
                e.target.checked ?
                    updatedUsersID.push(userID) :
                    updatedUsersID.splice(updatedUsersID.indexOf(userID), 1);
                return {
                    ...obj,
                    users_id: updatedUsersID
                };
            } else return obj;
        }));
    }

    const handlePurchaseClick = (activityID, itemID, userID) => {
        // setCurrActivities((currActivities.find(obj => obj.activity_id === activityID).items)

        const activity = {...currActivities.find(activity => activity.activity_id === activityID)};
        const item = activity.items.find(item => item.item_id === itemID);
        item.bought_by = userID;

        setCurrActivities({
            ...activities,
            items: {item}
        })

        // setCurrActivities(prevState =>
        //     prevState.filter(activity => activity.actitity_id === activityID)
        //         .map(obj => {
        //             if (obj.item_id === itemID) {
        //                 return {
        //                     ...activity,
        //                     items: {
        //                         ...obj.items,
        //                         bought_by: userID
        //                     }
        //                 }
        //             } else return obj;
        //         }));
    }

    console.log(currActivities);

    return (
        <div className="App">
            {/*<header className="App-header"></header>*/}
            <h1>{currParty.name}</h1>
            <PartyTable users={currUsers}
                        activities={currActivities}
                        handleActivityClick={handleActivityClick}
                        handleParticipationClick={handleParticipationClick}/>
            {selectedActivity ? <ActivityTable users={currUsers}
                                               selectedActivity={selectedActivity}
                                               handlePurchaseClick={handlePurchaseClick}/> :
                // <ActivityTable users={currUsers}
                //                items={currItems}
                //                handlePurchaseClick={handlePurchaseClick}/>
                <div></div>
            }
        </div>
    );
}

export default App;
