function ParticipationCheckbox(props) {
    const handleClick = (e) => {
        props.handleCheckboxClick(e);
    }
    return <td><input type="checkbox"
                      defaultChecked={props.defaultChecked}
                      value={[props.activityID, props.userID]}
                      onClick={handleClick}/></td>
}

function HeaderRowCell(props) {
    return <th>{props.value}</th>
}

function RowCell(props) {
    return <td>{props.value}</td>
}

function TotalAndDebt(props) {
    const totalExpenses = Object.entries(props.expenses);
    // artificially added activity users break the table
    return (
        <tr>
            <th><i>Total</i></th>
            {totalExpenses.map(expense => <RowCell key={expense[0]} value={expense[1]}/>)}
        </tr>
    );
}

function ActivityRow(props) {
    const partyUsersID = props.partyUsersID;
    const activityID = props.activityID;
    const activityName = props.activityName;
    const activityUsers = props.activityUsers;
    const activityExpense = props.activityExpense;
    let checkboxes = [];

    // console.log(partyUsersID);
    // console.log(activityUsers);
    for (const userID of partyUsersID) {
        // console.log(activityName, userID, partyUsersID[userID], userID in activityUsers);
        checkboxes.push(<ParticipationCheckbox key={activityID + userID}
                                               handleCheckboxClick={props.handleCheckboxClick}
                                               activityID={activityID}
                                               userID={partyUsersID[userID]}
                                               defaultChecked={userID-1 in activityUsers}/>);
    }
    return (
        <tr>
            <HeaderRowCell key={activityName} value={activityName}/>
            {checkboxes}
            <RowCell key={activityID} value={activityExpense}/>
        </tr>
    );
}

function Activities(props) {
    const activities = props.activities;
    const users = props.users;
    const partyUsersID = users && users.map(user => user.user_id);

    let activitiesRows = [];
    let totalUserExpenses = Object.fromEntries(partyUsersID.map(userID => [userID, 0]));
    console.log(totalUserExpenses);

    for (const activity of activities) {
        const activityID = activity.activity_id;
        const activityName = activity.activity_name;
        const activityUsers = activity.users_id;
        const activityExpense = activity.activity_expense;
        activitiesRows.push(<ActivityRow key={activityID}
                                         activityID={activityID}
                                         activityName={activityName}
                                         activityUsers={activityUsers}
                                         activityExpense={activityExpense}
                                         partyUsersID={partyUsersID}
                                         handleCheckboxClick={props.handleCheckboxClick}
        />);

        const partialExpense = activityExpense / activityUsers.length;
        for (const userID of activityUsers) {
            totalUserExpenses[userID] += partialExpense;
        }
    }

    for (const userID in totalUserExpenses) {
        totalUserExpenses[userID] = Math.round(totalUserExpenses[userID] * 100) / 100;
    }

    activitiesRows.push(<TotalAndDebt key={-1} expenses={totalUserExpenses}/>)

    return (activitiesRows);
}

export default Activities;

