    const selectedActivity = props.selectedActivity;
    // users only members from selected act
    // useEffect(() => {
    // }, [props.selectedActivity]);
    // useEffect(() => {
    //
    // }, [selectedActivity]);
    const currentUsers = props.users.filter(user => user.user_id in selectedActivity.users_id);
    console.log(currentUsers.map(user => user.user_id));
    return (
        <div>
            <h2>{selectedActivity && selectedActivity.activity_name}</h2>
            <table className="item-table">
                <thead>
                <UsersRow users={currentUsers}/>
                </thead>
                <tbody>
                <Items activity={props.selectedActivity}
                       // users={props.users}
                    // items={props.items || selectedActivity.items}
                       handleCheckboxClick={props.handlePurchaseClick}/>
                </tbody>
            </table>
        </div>
    )
}

export default ActivityTable;
