import React, {useState, useEffect} from "react";
import UsersRow from "./UsersRow";
import Activities from "./ActivitiesColumn";


function Table() {

    const [currUsers, setCurrUsers] = useState([]);
    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_members[0].users));
    }

    const [currActivities, setCurrActivities] = useState([]);
    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities[0].activities));
    }

    useEffect(() => getCurrUsers(), []);
    useEffect(() => getCurrActivities(), []);

    // const currActivities = ['son', 'buhlo', 'sport'];
    console.log(currActivities);

    return (
        <table>
            <thead>
            <UsersRow users={currUsers}/>
            </thead>
            <tbody>
            <Activities activities={currActivities}/>
            </tbody>
        </table>
    );
}

export default Table;
