        <tr>

        </tr>
    );
}

function ActivityRow(props) {
    const usersID = props.partyUsersID;
    var checkboxes = [];

    for (const userID of usersID) {
        if (true) {
            checkboxes.push(<ParticipationCheckbox/>);
        }
    }

    return (
        <tr>
            <td>{props.activityName}</td>
            {checkboxes}
            <td>{props.activityTotalPrice}</td>
        </tr>
    );
}

