// right-side panel in partyTable
export function calcActivityExpenses(activities, items) {
    let activityExpenses = {};

    for (const activity of activities) {
        activityExpenses[activity.activity_id] = 0;
        const activityItems = items.filter(item => activity.items_id.includes(item.item_id));
        for (const item of activityItems) {
            activityExpenses[activity.activity_id] += item.item_price;
        }
    }
    return activityExpenses;
}

// total row in partyTable
export function calcTotalExpensesPerUser(activities, activityExpenses) {
    let totalExpensesPerUser = {};

    for (const activity of activities) {

        const partialExpense = activityExpenses[activity.activity_id] / activity.users_id.length;

        for (const userID of activity.users_id) {
            totalExpensesPerUser[userID] += partialExpense;
        }
    }
    return totalExpensesPerUser;
}

