import React, {useEffect, useState} from "react";
import './App.css';
import PartyTable from "./PartyTable"
import ActivityTable from "./ActivityTable"


function App() {

    const [currUsers, setCurrUsers] = useState([]);
    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users));
    }

    const [currActivities, setCurrActivities] = useState([]);
    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities));
    }

    const updateActivityUsers = ({activityID, usersID}) => {
        fetch(`/change_activity/${activityID}`, {
            method: 'PATCH',
            body: JSON.stringify({
                "users_id": usersID
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
            .then(response => response.json())
            .then(json => {
                getCurrActivities();
            })
    }

    // updateActivityUsers(1, 2);

    useEffect(() => getCurrUsers(), []);
    useEffect(() => getCurrActivities(), []);

    // edit mode

    return (
        <div className="App">
            {/*<header className="App-header"></header>*/}
            <div className="bukh">
                <h1>Party</h1>
                <PartyTable users={currUsers} activities={currActivities}/>
                <h1>Activity</h1>
                <ActivityTable users={currUsers} activities={currActivities}/>
            </div>
        </div>
    );
}

export default App;
