function User(props) {
    return <th>{props.value.toString()}</th> // + edit buttons
}

export function UsersRow(props) {
    const users = props.users;
    return (
        <tr>
            <th>X</th>
            {users && users.map(user=> <User key={user.user_id} value={user.username}/>)}
            <th><i>Expenses</i></th>
        </tr>
        // TODO: Expenses/price options
    );
}

export function TotalRow(props) {
    const totalExpenses = Object.entries(props.expenses);
    // artificially added activity users break the table
    return (
        <tr>
            <th><i>Total</i></th>
            {totalExpenses.map(expense => <RowCell key={expense[0]} value={expense[1]}/>)}
        </tr>
    );
}

export function HeaderRowCell(props) {
    return <th>{props.value.toString()}</th>
}

export function RowCell(props) {
    return <td>{props.value.toString()}</td>
}

export function ParticipationCheckbox(props) {
    const handleClick = (e) => {
        props.handleCheckboxClick(e);
    }
    return <td><input type="checkbox"
                      defaultChecked={props.defaultChecked}
                      value={[props.activityID, props.userID]}
                      onClick={handleClick}/></td>
}

// TODO: unite
export function PurchaseCheckbox(props) {
    const handleClick = (e) => {
        props.handleCheckboxClick(e);
    }
    return <input type="radio"
                      defaultChecked={props.defaultChecked}
                      value={[props.itemID, props.userID]}
                      onClick={handleClick}/>
}
