import React, {useState, useEffect} from "react";
import UsersRow from "./UsersRow";
import Activities from "./Activities";


function Table() {

    const [currUsers, setCurrUsers] = useState([]);
    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users[0].users));
    }

    const [currActivities, setCurrActivities] = useState([]);
    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities[0].activities));
    }

    useEffect(() => getCurrUsers(), []);
    useEffect(() => getCurrActivities(), []);

    // handleAddUser
    // handleAddActivity
    // handleCheckboxChange
    console.log(currUsers);

    return (
        <div className="party-table">
            <table>
                <thead>
                <UsersRow users={currUsers}/>
                </thead>
                <tbody>
                <Activities activities={currActivities}/>
                </tbody>
            </table>
        </div>
    );
}

export default Table;
