function UserCell(props) {
    return <th>{props.value.toString()}</th> // + edit buttons
}

export function UsersRow(props) {
    const users = props.users;
    return (
        <tr>
            <th>X</th>
            {users.map(user => <UserCell key={user.user_id} value={user.username}/>)}
            <th><i>Expenses</i></th>
        </tr>
        // TODO: Expenses/price options
    );
}

// move expense calculation logic to a separate module;
// always round numbers to to symbols after the period
export function TotalRow(props) {
    // artificially added activity users break the table
    // assert(totalExpenses.length == props.usersID.length)
    const usersExpensesEntries = Object.entries(props.usersExpenses);
    return (
        <tr>
            <th><i>{props.rowName && 'Total'}</i></th>
            {usersExpensesEntries.map(expense => <RowCell key={expense[0]} value={expense[1]}/>)}
        </tr>
    );
}

