import React, {useEffect, useState} from "react";
import './App.css';
import Main from "./Main";


function App() {
    const [currParty, setCurrParty] = useState([]);

    // get party from the database once and keep users, activities as stateful components
    const loadCurrParty = () => {
        fetch('/get_recent_party')
            .then(res => res.json())
            .then(json => setCurrParty(json))
            // .then(json => {currParty = json})
            .catch(ex => console.log(ex));
    }

    useEffect(() => {
        loadCurrParty();
    }, []);

    console.log(currParty);

    return (
        <Main currParty={currParty}/>
    );
}

export default App;
