import {HeaderRowCell, ActivityRowCell, RowCell, ParticipationCheckbox, TotalRow} from "./TableElements";

function DebtRow(props) {
    return (<tr>
        <th><i>Debt</i></th>
    </tr>)
}

function ActivityRow(props) {
    let checkboxes = [];
    for (const userID of props.partyUsersID) {
        checkboxes.push(<ParticipationCheckbox key={props.activityID + userID}
                                               handleCheckboxClick={props.handleCheckboxClick}
                                               activityID={props.activityID}
                                               userID={userID}
                                               defaultChecked={props.activityUsers.includes(userID)}/>);
    }
    return (
        <tr>
            <ActivityRowCell key={props.activityName}
                             activityID={props.activityID}
                             value={props.activityName}
            handleActivityClick={props.handleActivityClick}/>
            {checkboxes}
            <RowCell key={props.activityID} value={props.activityExpense}/>
        </tr>
    );
}

// separate function for expenses and debt calculation

function Activities(props) {
    const activities = props.activities;
    const users = props.users;
    const partyUsersID = users && users.map(user => user.user_id);

    let activitiesRows = [];
    let totalUserExpenses = Object.fromEntries(partyUsersID.map(userID => [userID, 0]));

    // debt = totalexpenses

    for (const activity of activities) {
        const activityID = activity.activity_id;
        const activityName = activity.activity_name;
        const activityUsers = activity.users_id;
        const activityExpense = activity.activity_expense;
        const activityItems = activity.items;
        // iterate over items, calc the sum; subtract items - total
        // what if unfolding

        activitiesRows.push(<ActivityRow key={activityID}
                                         activity={activity}
                                         activityID={activityID}
                                         activityName={activityName}
                                         activityUsers={activityUsers}
                                         activityExpense={activityExpense}
                                         partyUsersID={partyUsersID}
                                         handleCheckboxClick={props.handleCheckboxClick}
        />);

        const partialExpense = activityExpense / activityUsers.length;
        let totalDebtExpenses = Object.fromEntries(activityUsers.map(userID => [userID, 0]));
        for (const userID of activityUsers) {
            totalUserExpenses[userID] += partialExpense;
            for (const item of activityItems) {
                totalDebtExpenses[item.bought_by] += item.item_price;
            }
        }
    }

    for (const userID in totalUserExpenses) {
        totalUserExpenses[userID] = Math.round(totalUserExpenses[userID] * 100) / 100;
    }

    activitiesRows.push(<TotalRow key={-1} expenses={totalUserExpenses}/>);

    return (activitiesRows);
}

export default Activities;
