import {HeaderRowCell, RowCell} from "./TableElements";

function PurchaseCheckbox(props) {
    const handleClick = (e) => {
        props.handleCheckboxClick(e);
    }
    return <td><input type="checkbox"
                      defaultChecked={props.defaultChecked}
                      value={[props.itemID, props.userID]}
                      onClick={handleClick}/></td>
}

function ItemRow(props) {
    const partyUsersID = props.partyUsersID;

    let checkboxes = [];
    for (const userID of partyUsersID) {
        checkboxes.push(<PurchaseCheckbox key={props.activityID + userID}
                                          handleCheckboxClick={props.handleCheckboxClick}
                                          itemID={props.itemID}
                                          userID={props.userID}
                                          defaultChecked={props.activityUsers.includes(userID)}/>);
    }
    return (
        <tr>
            <HeaderRowCell key={props.itemName} value={props.itemName}/>
            {checkboxes}
            <RowCell key={props.itemID} value={props.itemPrice}/>
        </tr>
    );
}

function Items(props) {
    const items = props.items;
    const users = props.users;
    const partyUsersID = users && users.map(user => user.user_id);

    let itemsRows = [];

    for (const item of items) {
        const itemID = item.item_id;
        const itemName = item.item_name;
        const itemBoughtBy = item.bought_by;
        const itemPrice = item.item_price;
        itemsRows.push(<ItemRow key={itemID}
                                itemID={itemID}
                                itemName={itemName}
                                itemBoughtBy={itemBoughtBy}
                                itemPrice={itemPrice}
                                partyUsersID={partyUsersID}
                                handleCheckboxClick={props.handleCheckboxClick}
        />);
    }
    return (itemsRows);
}

export default Items;
