import React, {useState, useEffect} from "react";
import UsersRow from "./UsersRow";
import Activities from "./Activities";


function PartyTable() {
    const [currUsers, setCurrUsers] = useState([]);
    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users));
    }

    const [currActivities, setCurrActivities] = useState([]);
    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities));
    }

    useEffect(() => getCurrUsers(), []);
    useEffect(() => getCurrActivities(), []);

    const updateActivityUsers = ({acitvityID, usersID}) => {
        fetch('/change_activity', {
            method: 'PATCH',
            body: JSON.stringify({
                "users_id": usersID
            }),
        })
    }

    const handleCheckboxClick = (e) => {
        const [activityID, userID] = e.target.value;
        const usersID = currActivities[activityID].users_id;
        e.target.checked ?
            console.log(currActivities[activityID].users_id.push(userID)) :
            // setCurrActivities(currActivities[activityID].users_id.push(userID)) :
            setCurrActivities(currActivities[activityID].users_id.splice(usersID.indexOf(userID)))
            console.log('unchecked');
    }

    // handleAddUser
    // handleAddActivity
    return (
        <div>
            <table className="party-table">
                <thead>
                <UsersRow users={currUsers}/>
                </thead>
                <tbody>
                <Activities activities={currActivities}
                            users={currUsers}
                            handleCheckboxClick={handleCheckboxClick}/>
                </tbody>
            </table>
        </div>
    );
}

export default PartyTable;
