function Activity(props) {
    return <tr>{props.value}</tr>
}

function ActivityTotalPrice(props) {
    return <tr>{props.value}</tr>
}

function ParticipationCheckbox(props) {
    // onClick = () => TableHandler(user_id, party_id)
    return <td><input type="checkbox"/></td>
}

function Activities(props) {
    const activities = props.activities;
    const activitiesNames = activities && activities.map(activity => activity.activity_name.toString())
    const activitiesUsers = activities && activities.map(activity => activity.users_id.toString())
    const activitiesTotalPrice = activities && activities.map(activity => activity.total_price.toString())

    return (
        <tr>
            { activitiesNames &&  activitiesNames.map(activityName => <Activity key={activityName.toString()}
                                                                                    value={activityName}/>)}
            {activitiesUsers && activitiesUsers.map(activityUsers => <ParticipationCheckbox
                                                                                 value={activityUsers}/>)}
            { activitiesTotalPrice &&  activitiesTotalPrice.map(activityTotalPrice => <ActivityTotalPrice key={activityTotalPrice.toString()}
                                                                                 value={activityTotalPrice}/>)}
        </tr>
    );
}

export default Activities;
