import React from "react";

class Users extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: ['Arina', 'Max', 'Sonya']
        }
        this.handleAddUser = this.handleAddUser.bind(this);
    }

    handleAddUser(e) {
        this.setState(prevState => {prevState.users.push(e.target.value)})
    }

    render() {
        return (
            <tr>
                {this.state.users.map(user => <th>{user}</th>)}
                <input type="text" onSubmit={this.handleAddUser}/>
            </tr>
        );
    }
}

export default Users;
