// right-side panel in partyTable
export function calcActivityExpenses(activities, items) {
    let activityExpenses = {};

    for (const activity of activities) {
        activityExpenses[activity.activity_id] = 0;
        const activityItems = items.filter(item => activity.items_id.includes(item.item_id));
        for (const item of activityItems) {
            activityExpenses[activity.activity_id] += item.item_price;
        }
    }
    return activityExpenses;
}

// total & debt row in partyTable
export function calcTotalUsersFinance(users, activities, items, activityExpenses) {
    let totalUsersFinance = Object.fromEntries(users.map(user => [user.user_id, 0]));

    for (const activity of activities) {

        const partialExpense = activityExpenses[activity.activity_id] / activity.users_id.length;
        const activityItems = items.filter(item => activity.items_id.includes(item.item_id));

        for (const userID of activity.users_id) {
            totalUsersFinance[userID]['expenses'] += partialExpense;
            totalUsersFinance[userID]['debt'] += partialExpense;
        }
        for (const item of activityItems) {
            totalUsersFinance[item.bought_by]['debt'] -= item.item_price;
        }
    }

    for (const userID in totalUsersFinance) {
        totalUsersFinance[userID]['expenses'] = Math.round(totalUsersFinance[userID]['expenses'] * 100) / 100;
        totalUsersFinance[userID]['debt'] = Math.round(totalUsersFinance[userID]['debt'] * 100) / 100;
    }
    return totalUsersFinance;
}


// total row in activityTable
export function calcActivityUserExpenses(items) {
    let activityUserExpenses = {};
    for (const item of items) {
        activityUserExpenses[item.bought_by] += item.item_price;
    }
    return activityUserExpenses;
}
