function Activities(props) {
    const activities = props.activities;
    const activitiesNames = activities && activities.map(activity => activity.activity_name.toString())
    const activitiesUsers = activities && activities.map(activity => activity.users_id)
    const activitiesTotalPrice = activities && activities.map(activity => activity.total_price.toString())
    console.log(activitiesUsers);

    for (const activity in activities) {
        const activityName = activity.activity_name;
        <Activity key={activityName}/>
    }

        return (

        <tr>
            {activitiesNames && activitiesNames.map(activityName => <Activity key={activityName.toString()}
                                                                              activityName={activityName}/>)}
            {activitiesUsers && activitiesUsers.map(activityUsers => <ParticipationCheckbox
                usersID={activityUsers}/>)}
            {activitiesTotalPrice && activitiesTotalPrice.map(activityTotalPrice => <ActivityTotalPrice
                key={activityTotalPrice.toString()}
                totalPrice={activityTotalPrice}/>)}
        </tr>
    );
}

export default Activities;
