import React from "react";

function User(props) {
    return <th>{props.value}</th> // + edit buttons
}

class UsersRow extends React.Component {
    constructor(props) {
        super(props);
        // this.state = {
        //     users: ['Arina', 'Max', 'Sonya']
        // }
        this.handleAddUser = this.handleAddUser.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleAddUser(e) {
        this.setState(prevState => {prevState.users.push(e.target.value)})
        if (e.keyCode === 13) {
            e.preventDefault(); // ?
            console.log('ekkkkek')
        }

    }

    handleChange(e) {
        this.props.onUsersRowChange(e.target.value);
    }

    render() {
        const usernames = this.props.usernames;
        return (
            <tr>
                {usernames.map(user => <User key={user.toString()} value={user}/>)}
                {/*<input type="text" onSubmit={this.handleAddUser}/>*/}
            </tr>
        );
    }
}

export default UsersRow;
