import React, {useEffect, useState} from "react";
import './App.css';
import PartyTable from "./PartyTable"
import ActivityTable from "./ActivityTable"


function App() {
    const [currParty, setCurrParty] = useState([]);
    const [currUsers, setCurrUsers] = useState([]);
    const [currActivities, setCurrActivities] = useState([]);
    const [selectedActivity, setSelectedActivity] = useState({});
    // const [selectedActivity, setSelectedActivity] = useState({
    //     activity_name: "nnn",
    //     users_id: [1, 2, 3],
    //     items: [
    //         {
    //             "item_id": 3,
    //             "item_name": "lamp",
    //             "item_price": 100,
    //             "bought_by": 1
    //         }
    //     ]
    // });
    const [currItems, setCurrItems] = useState([]);

    const getCurrParty = () => {
        fetch('/get_recent_party')
            .then(res => res.json())
            .then(json => setCurrParty(json))
            .catch(ex => console.log(ex));
    }

    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users));
    }

    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities));
    }

    const getCurrItems = () => {
        fetch('/get_items')
            .then(res => res.json())
            .then(json => setCurrItems(json.items));
    }

    // const getCurrUsers = () => {
    //     setCurrUsers(currParty.users);
    // }
    //
    // const getCurrActivities = () => {
    //     setCurrActivities(currParty.activities);
    // }
    //
    const getSelectedActivity = () => {
        setSelectedActivity((currActivities) => {return (currActivities[0])});
    }

    // const getSelectedActivity = () => {
    //     setSelectedActivity({activity_name: "nnn"});
    // }
    //
    // const getCurrItems = () => {
    //     setCurrItems(selectedActivity.items);
    // }

    useEffect(() => {
        getCurrParty();
        getCurrUsers();
        getCurrActivities();
        getSelectedActivity();
        getCurrItems();
    }, []);

    // last_viewed patch request
    // edit mode
    // jsons may be and are empty in the beginning

    // be good without e argument and check by presence of user in array
    const handleParticipationClick = (e, activityID, userID) => {
        setCurrActivities([...currActivities].map(obj => {
            if (obj.activity_id === activityID) {
                const updatedUsersID = obj.users_id;
                e.target.checked ?
                    updatedUsersID.push(userID) :
                    updatedUsersID.splice(updatedUsersID.indexOf(userID));
                return {
                    ...obj,
                    users_id: updatedUsersID
                }
            } else return obj;
        }));
    }

    const handlePurchaseClick = (itemID, userID) => {
        setCurrItems([...currItems].map(obj => {
            if (obj.item_id === itemID) {
                return {
                    ...obj,
                    bought_by: userID
                }
            } else return obj;
        }));
    }
    // console.log(currActivities);

    return (
        // <p>{currActivities[0].activity_name}</p>
        <div className="App">
            {/*<header className="App-header"></header>*/}
            <div className="bukh">
                <h1>{currParty.name}</h1>
                <PartyTable users={currUsers}
                            activities={currActivities}
                            handleParticipationClick={handleParticipationClick}/>
                <h1>{selectedActivity.activity_name}</h1>
                <ActivityTable users={currUsers}
                               items={currItems}
                               // selectedActivity={selectedActivity}
                               handlePurchaseClick={handlePurchaseClick}/>
            </div>
        </div>
    );
}

export default App;
