import {HeaderRowCell, RowCell, PurchaseCheckbox, TotalRow} from "./TableElements";
import {calcActivityUserExpenses} from "./Calculations";


function ItemRow(props) {
    let checkboxes = [];
    for (const userID of props.activityUsersID) {
        checkboxes.push(<PurchaseCheckbox key={props.itemID + userID}
                                          handleCheckboxClick={props.handleCheckboxClick}
                                          activityID={props.activityID}
                                          itemID={props.itemID}
                                          userID={userID}
                                          defaultChecked={userID === props.itemBoughtBy}/>);
    }
    return (
        <tr>
            <HeaderRowCell key={props.itemName} value={props.itemName}/>
            {checkboxes}
            <RowCell key={props.itemID} value={props.itemPrice}/>
        </tr>
    );
}


function Items(props) {
    // const users = props.users;
    // const partyUsersID = users && users.map(user => user.user_id);
    const activity = props.activity;
    const activityUsersID = activity.users_id;

    let itemsRows = [];
    // let totalUserExpenses = Object.fromEntries(activityUsersID.map(userID => [userID, 0]));

    const activityUsersExpenses = calcActivityUserExpenses(props.items);

    for (const item of props.items) {
        const itemID = item.item_id;
        const itemName = item.item_name;
        const itemBoughtBy = item.bought_by;
        const itemPrice = item.item_price;
        itemsRows.push(<ItemRow key={itemID}
                                activityID={activity.activity_id}
                                itemID={itemID}
                                itemName={itemName}
                                itemBoughtBy={itemBoughtBy}
                                itemPrice={itemPrice}
                                activityUsersID={activityUsersID}
                                handleCheckboxClick={props.handleCheckboxClick}
        />);
        // totalUserExpenses[itemBoughtBy] += itemPrice;
    }

    // itemsRows.push(<TotalRow key={-1} rowName={'Total'} expenses={totalUserExpenses}/>)
    itemsRows.push(<TotalRow key={-1} rowName={'Total'} expenses={activityUsersExpenses}/>)

    return itemsRows;
}

export default Items;
