import {useState} from "react";
import Items from "./Items";
import {UsersRow} from "./TableElements";


function ActivityTable(props) {

    // const selectedActivity = props.selectedActivity;
    // users only members from selected act
    return (
    <div>
        <h2>{props.selectedActivity.activity_name}</h2>
            <table className="item-table">
                <thead>
                <UsersRow users={props.users}/>
                </thead>
                <tbody>
                <Items items={props.selectedActivity && props.selectedActivity.items}
                       users={props.users}
                       handleCheckboxClick={props.handlePurchaseClick}/>
                </tbody>
            </table>
        </div>
    )
}

export default ActivityTable;
