function ParticipationCheckbox() {
    // onClick = () => TableHandler(user_id, party_id)
    return <td><input type="checkbox"/></td>
}

function TotalCell(props) {
    return <td>{props.value}</td>
}

function TotalAndDebt(props) {
    const totalExpenses = Object.entries(props.expenses);
    return (
        <tr>
            <th>Total</th>
            {totalExpenses.map(expense => <TotalCell key={expense[0]} value={expense[1]}/>)}
        </tr>
    );
}

function ActivityRow(props) {
    const usersID = props.partyUsersID;
    const activityID = props.activityID;
    let checkboxes = [];

    for (const userID of usersID) {
        checkboxes.push(<ParticipationCheckbox key={activityID + userID}/>);
    }
    return (
        <tr>
            <td>{props.activityName}</td>
            {checkboxes}
            <td>{props.activityExpense}</td>
        </tr>
    );
}

function Activities(props) {
    const activities = props.activities;
    const users = props.users;
    const partyUsersID = users && users.map(user => user.user_id);

    let activitiesRows = [];
    let totalUserExpenses = {};

    for (const activity of activities) {
        const activityID = activity.activity_id;
        const activityName = activity.activity_name;
        const activityUsers = activity.users_id;
        const activityExpense = activity.activity_expense;
        activitiesRows.push(<ActivityRow key={activityID}
                                         activityID={activityID}
                                         activityName={activityName}
                                         activityUsers={activityUsers}
                                         activityExpense={activityExpense}
                                         partyUsersID={partyUsersID}/>);

        const partialExpense = activityExpense / activityUsers.length;
        for (const userID of activityUsers) {
            if (!(userID in totalUserExpenses)) {
                totalUserExpenses[userID] = 0;
            }
            totalUserExpenses[userID] += partialExpense;
        }
    }

    for (const userID in totalUserExpenses) {
        totalUserExpenses[userID] = Math.round(totalUserExpenses[userID] * 100) / 100;
    }

    activitiesRows.push(<TotalAndDebt expenses={totalUserExpenses}/>)

    return (activitiesRows);
}

export default Activities;
