import React, {useEffect, useState} from "react";
import './App.css';
import Main from "./Main";


function App() {
    // const [currParty, setCurrParty] = useState([]);

    // get party from the database once and keep users, activities as stateful components
    let currParty = {};
    const loadCurrParty = () => {
        fetch('/get_recent_party')
            .then(res => res.json())
            // .then(json => setCurrParty(json))
            .then(json => {return json;})
            .catch(ex => console.log(ex));
    }

    useEffect(() => {
        let mounted = true;
        if (mounted) {
            currParty = loadCurrParty();
        }
        return () => {
            mounted = false;
        };
    }, []);

    console.log(currParty);

    return (
        <div>
            {currParty && <Main currParty={currParty}/>}
        </div>
    );
}

export default App;
