import React, {useState, useEffect} from "react";
import logo from './logo.svg';
import './App.css';
import Table from "./Table"
import Activities from "./Table"
import UsersRow from "./Users";

// import
const API_HOST = 'http://localhost:3000'
const API_URL = '${API_HOST}/activities'

function App() {
    const [currTime, setCurrTime] = useState(0);
    useEffect(() => {
        fetch('http://localhost:5000/').then(res => res.json()).then(data => {
            setCurrTime(data.time);
        });
    }, []);

    return (
        <div className="App">
            {/*<header className="App-header"></header>*/}
            <p>lol: {currTime}</p>
            <h1>Party</h1>
            <div id="table">
                <Table/>
            </div>

            <table>
                <thead>
                <UsersRow/>
                <tr>
                    <th>Anton</th>
                    <th>Max</th>
                    <th>Sonya</th>
                </tr>
                </thead>
                <tbody>
                <tr>
                    <td><input type="checkbox"/></td>
                </tr>
                </tbody>
            </table>
        </div>
    );
}

export default App;
