import React from "react";
import {UsersRow} from "./TableElements";
import Activities from "./Activities";


function PartyTable(props) {
    const handleParticipationClick = (e) => {
        const [activityID, userID] = e.target.value;
        const usersID = props.currActivities[activityID].users_id;
        e.target.checked ?
            setCurrActivities([...currActivities].map(obj => {
                if (obj.activity_id === activityID) {
                    return {
                        ...obj,
                        users_id: [1, 2, 3, 4]
                    }
                }
            })) :
            // setCurrActivities(currActivities[activityID].users_id.splice(usersID.indexOf(userID)))
            console.log('unchecked');
    }

    // handleAddUser
    // handleAddActivity
    return (
        <div>
            <table className="party-table">
                <thead>
                <UsersRow users={props.users}/>
                </thead>
                <tbody>
                <Activities activities={props.activities}
                            users={props.users}
                            handleCheckboxClick={handleParticipationClick}/>
                </tbody>
            </table>
        </div>
    );
}

export default PartyTable;
