import {useEffect, useState} from "react";
import Items from "./Items";
import {UsersRow} from "./TableElements";


function ActivityTable() {

    const [currUsers, setCurrUsers] = useState([]);
    const getCurrUsers = () => {
        fetch('/get_users')
            .then(res => res.json())
            .then(json => setCurrUsers(json.party_users));
    }

    const [currActivities, setCurrActivities] = useState([]);
    const getCurrActivities = () => {
        fetch('/get_activities')
            .then(res => res.json())
            .then(json => setCurrActivities(json.activities));
    }

    const [currItems, setCurrItems] = useState([]);
    const getCurrItems = () => {
        fetch('/get_items')
            .then(res => res.json())
            .then(json => setCurrItems(json.items));
    }

    useEffect(() => getCurrUsers(), []);
    useEffect(() => getCurrActivities(), []);
    useEffect(() => getCurrItems(), []);

    const handlePurchaseClick = (e) => {

    }

    return (
        <div>
            <table className="item-table">
                <thead>
                <UsersRow users={currUsers}/>
                </thead>
                <tbody>
                <Items activities={currActivities}
                       items={currItems}
                       users={currUsers}
                       handleCheckboxClick={handlePurchaseClick}/>
                </tbody>
            </table>
        </div>
    )
}

export default ActivityTable;
